];

// --- INPUT ---
// This code expects the final JSON object from the previous step.
// Ensure this path matches the output of your Gemini/Code node.
const resumeData = $input.item.json.output;

// --- CSS STYLING ---
// This CSS provides a clean, professional, and ATS-friendly design.
// You can customize fonts, colors, and spacing here.
// --- UPDATED CSS STYLING ---
// This new style uses a more traditional serif font (Garamond) and a clean, structured layout
// suitable for a finance professional's resume.
const cssStyle = `
<style>
    /* --- General Body & Typography --- */
    body {
        font-family: Garamond, 'Times New Roman', serif;
        font-size: 11pt;
        line-height: 1.4;
        color: #1a1a1a;
        margin: 0;
        padding: 0;
    }
    .container {
        max-width: 850px;
        margin: 10px auto;
        padding: 40px;
    }

    /* --- Header & Contact Info --- */
    h1 {
        font-size: 26pt;
        color: #111;
        margin-bottom: 5px;
        font-weight: 600;
        text-align: center;
        letter-spacing: 2px;
    }
    .contact-info {
        font-size: 10pt;
        color: #444;
        margin-bottom: 25px;
        text-align: center;
    }
    .contact-info a {
        color: #005a99;
        text-decoration: none;
    }
    .contact-info span:not(:last-child)::after {
        content: "|";
        margin: 0 10px;
    }

    /* --- Professional Summary --- */
    .summary {
        text-align: center;
        margin-bottom: 25px;
        font-style: italic;
    }

    /* --- Section Titles --- */
    h2 {
        font-size: 13pt;
        color: #222;
        border-bottom: 1.5px solid #444;
        padding-bottom: 4px;
        margin-top: 25px;
        margin-bottom: 20px;
        font-weight: bold;
        text-transform: uppercase;
        letter-spacing: 1.5px;
    }

    /* --- Projects, Experience & Education Layout --- */
    .entry {
        margin-bottom: 20px;
    }
    .entry-header {
        display: flex;
        justify-content: space-between;
        align-items: baseline;
        margin-bottom: 4px;
    }
    h3 {
        font-size: 12pt;
        color: #1a1a1a;
        font-weight: bold;
        margin: 0;
    }
    .sub-header {
        font-style: italic;
        color: #333;
        margin: 0;
    }
    .dates {
        font-weight: normal;
        color: #555;
        font-size: 10pt;
        font-style: italic;
    }
    ul {
        padding-left: 18px;
        margin-top: 5px;
        list-style-type: disc;
    }
    li {
        margin-bottom: 6px;
    }

    /* --- Skills Section --- */
    .skills-section p {
        margin: 5px 0;
    }
    .skills-section strong {
        font-weight: bold;
        color: #333;
    }
</style>
`;

// --- HTML GENERATION LOGIC ---

// Helper function to check if a value exists
const exists = (val) => val && val.length > 0;

// 1. Build Personal Info Header
let personalInfoHtml = `<h1>${resumeData.personal_info.full_name}</h1>`;
personalInfoHtml += `<div class="contact-info">`;
if (exists(resumeData.personal_info.email)) {
    personalInfoHtml += `<span>${resumeData.personal_info.email}</span>`;
}
if (exists(resumeData.personal_info.phone_number)) {
    personalInfoHtml += `<span>${resumeData.personal_info.phone_number}</span>`;
}
if (exists(resumeData.personal_info.linkedin_url)) {
    personalInfoHtml += `<span><a href="https://${resumeData.personal_info.linkedin_url}" target="_blank">${resumeData.personal_info.linkedin_url}</a></span>`;
}
personalInfoHtml += `</div>`;


// 2. Build Customized Summary
let summaryHtml = `<h2>Summary</h2><p>${resumeData.customized_summary}</p>`;

// 3. Build Work Experience Section
let experienceHtml = `<h2>Work Experience</h2>`;
resumeData.rewritten_work_experience.forEach(job => {
    experienceHtml += `
        <div class="job">
            <div class="job-header">
                <h3>${job.job_title} <span class="job-company">at ${job.company}</span></h3>
                <span class="job-dates">${job.start_date} – ${job.end_date}</span>
            </div>
            <ul>
                ${job.rewritten_responsibilities.map(resp => `<li>${resp}</li>`).join('')}
            </ul>
        </div>
    `;
});

// 4. Build Optimized Skills Section
let skillsHtml = `<h2>Skills</h2><div class="skills-section">`;
if (exists(resumeData.optimized_skills.technical)) {
    skillsHtml += `<p><span class="skills-category">Technical Skills:</span> ${resumeData.optimized_skills.technical.join(', ')}</p>`;
}
if (exists(resumeData.optimized_skills.soft)) {
    skillsHtml += `<p><span class="skills-category">Soft Skills:</span> ${resumeData.optimized_skills.soft.join(', ')}</p>`;
}
if (exists(resumeData.optimized_skills.tools)) {
    skillsHtml += `<p><span class="skills-category">Tools:</span> ${resumeData.optimized_skills.tools.join(', ')}</p>`;
}
skillsHtml += `</div>`;


// 5. Build Education Section
let educationHtml = `<h2>Education</h2>`;
resumeData.education.forEach(edu => {
    educationHtml += `
        <div class="job">
             <div class="job-header">
                <h3>${edu.degree}</h3>
                <span class="job-dates">${edu.graduation_date}</span>
            </div>
            <p style="margin-top: 2px;"><em>${edu.institution}</em></p>
        </div>
    `;
});

// --- FINAL ASSEMBLY ---
// Combine all parts into a full HTML document.
const finalHtml = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>${resumeData.personal_info.full_name} - Resume</title>
    ${cssStyle}
</head>
<body>
    <div class="container">
        ${personalInfoHtml}
        ${summaryHtml}
        ${experienceHtml}
        ${skillsHtml}
        ${educationHtml}
    </div>
</body>
</html>
`;

// --- OUTPUT ---
// Place the final HTML string into a new field called 'resumeHtml'.
$input.item.json.resumeHtml = finalHtml;
return $input.item;

const items = $input.all();
const resumeItem = items.find(item => item.binary.resume !== undefined);

if (resumeItem) {
  return {
    json: { processed: true },
    binary: {
      resume: resumeItem.binary.resume
    }
  };
}

return {
  json: { error: "No resume found" },
  binary: {}
};

